//! Centralized Python filename and stem constants.

/// Package initializer file name
pub const INIT_FILE: &str = "__init__.py";
/// Package initializer stem (attribute name)
pub const INIT_STEM: &str = "__init__";
/// Package main entry file name
pub const MAIN_FILE: &str = "__main__.py";
/// Package main entry stem
pub const MAIN_STEM: &str = "__main__";
